from datetime import datetimeimport syspath_host = "/etc/hosts"redirect = "127.0.0.1"website_list = ['www.reddit.com', "reddit.com"]time_zone = input('AM or PM: ')print("Put all times in double digits")print("EX: Hour = 08, minute = 03, second = 00 ")hour = input('Hour You Wanna Finish: ')min = input('Minute You Wanna Finish: ')sec = input('Second You Wanna Finish: ')if time_zone == "AM" or 'am':    end_time = hour + ":" + min + ":" + secelse:    int_hour = int(hour)    new_hour = int_hour + 12    str_hour = str(new_hour)    end_time = str_hour + ":" + min + ":" + secwhile True:    now = datetime.now()    current_time = now.strftime("%H:%M:%S")    if end_time < current_time:        print('invalid input')        continue    else:        if current_time == end_time:            with open(path_host, "r+") as file:                content = file.readlines()                file.seek(0)                for line in content:                    if not any(site in line for site in website_list):                        file.write(line)                file.truncate()            print('Breaktime Slave')            break        else:            with open(path_host, "r+") as file:                content = file.read()                for site in website_list:                    if site in content:                        pass                    else:                        file.write(redirect + " " + site + "\n")                print("Work Stupid")                break